v class='line' id='LC39'><span class="k">use</span> <span class="n">diagnostics</span><span class="p">;</span></div><div class='line' id='LC40'><span class="k">use</span> <span class="nn">File::</span><span class="n">Spec</span><span class="p">;</span></div><div class='line' id='LC41'><span class="k">use</span> <span class="nn">Getopt::</span><span class="n">Long</span><span class="p">;</span></div><div class='line' id='LC42'><br/></div><div class='line' id='LC43'><span class="c1"># Set up a few variables for use in the script</span></div><div class='line' id='LC44'><span class="k">my</span> <span class="nv">$tunerversion</span> <span class="o">=</span> <span class="s">&quot;1.2.0&quot;</span><span class="p">;</span></div><div class='line' id='LC45'><span class="k">my</span> <span class="p">(</span><span class="nv">@adjvars</span><span class="p">,</span> <span class="nv">@generalrec</span><span class="p">);</span></div><div class='line' id='LC46'><br/></div><div class='line' id='LC47'><span class="c1"># Set defaults</span></div><div class='line' id='LC48'><span class="k">my</span> <span class="nv">%opt</span> <span class="o">=</span> <span class="p">(</span></div><div class='line' id='LC49'>		<span class="s">&quot;nobad&quot;</span> 		<span class="o">=&gt;</span> <span class="mi">0</span><span class="p">,</span></div><div class='line' id='LC50'>		<span class="s">&quot;nogood&quot;</span> 		<span class="o">=&gt;</span> <span class="mi">0</span><span class="p">,</span></div><div class='line' id='LC51'>		<span class="s">&quot;noinfo&quot;</span> 		<span class="o">=&gt;</span> <span class="mi">0</span><span class="p">,</span></div><div class='line' id='LC52'>		<span class="s">&quot;nocolor&quot;</span> 		<span class="o">=&gt;</span> <span class="mi">0</span><span class="p">,</span></div><div class='line' id='LC53'>		<span class="s">&quot;forcemem&quot;</span> 		<span class="o">=&gt;</span> <span class="mi">0</span><span class="p">,</span></div><div class='line' id='LC54'>		<span class="s">&quot;forceswap&quot;</span> 	<span class="o">=&gt;</span> <span class="mi">0</span><span class="p">,</span></div><div class='line' id='LC55'>		<span class="s">&quot;host&quot;</span> 			<span class="o">=&gt;</span> <span class="mi">0</span><span class="p">,</span></div><div class='line' id='LC56'>		<span class="s">&quot;socket&quot;</span> 		<span class="o">=&gt;</span> <span class="mi">0</span><span class="p">,</span></div><div class='line' id='LC57'>		<span class="s">&quot;port&quot;</span> 			<span class="o">=&gt;</span> <span class="mi">0</span><span class="p">,</span></div><div class='line' id='LC58'>		<span class="s">&quot;user&quot;</span> 			<span class="o">=&gt;</span> <span class="mi">0</span><span class="p">,</span></div><div class='line' id='LC59'>		<span class="s">&quot;pass&quot;</span>			<span class="o">=&gt;</span> <span class="mi">0</span><span class="p">,</span></div><div class='line' id='LC60'>		<span class="s">&quot;skipsize&quot;</span> 		<span class="o">=&gt;</span> <span class="mi">0</span><span class="p">,</span></div><div class='line' id='LC61'>		<span class="s">&quot;checkversion&quot;</span> 	<span class="o">=&gt;</span> <span class="mi">0</span><span class="p">,</span></div><div class='line' id='LC62'>	<span class="p">);</span></div><div class='line' id='LC63'><br/></div><div class='line' id='LC64'><span class="c1"># Gather the options from the command line</span></div><div class='line' id='LC65'><span class="n">GetOptions</span><span class="p">(</span><span class="o">\</span><span class="nv">%opt</span><span class="p">,</span></div><div class='line' id='LC66'>		<span class="s">&#39;nobad&#39;</span><span class="p">,</span></div><div class='line' id='LC67'>		<span class="s">&#39;nogood&#39;</span><span class="p">,</span></div><div class='line' id='LC68'>		<span class="s">&#39;noinfo&#39;</span><span class="p">,</span></div><div class='line' id='LC69'>		<span class="s">&#39;nocolor&#39;</span><span class="p">,</span></div><div class='line' id='LC70'>		<span class="s">&#39;forcemem=i&#39;</span><span class="p">,</span></div><div class='line' id='LC71'>		<span class="s">&#39;forceswap=i&#39;</span><span class="p">,</span></div><div class='line' id='LC72'>		<span class="s">&#39;host=s&#39;</span><span class="p">,</span></div><div class='line' id='LC73'>		<span class="s">&#39;socket=s&#39;</span><span class="p">,</span></div><div class='line' id='LC74'>		<span class="s">&#39;port=i&#39;</span><span class="p">,</span></div><div class='line' id='LC75'>		<span class="s">&#39;user=s&#39;</span><span class="p">,</span></div><div class='line' id='LC76'>		<span class="s">&#39;pass=s&#39;</span><span class="p">,</span></div><div class='line' id='LC77'>		<span class="s">&#39;skipsize&#39;</span><span class="p">,</span></div><div class='line' id='LC78'>		<span class="s">&#39;checkversion&#39;</span><span class="p">,</span></div><div class='line' id='LC79'>		<span class="s">&#39;help&#39;</span><span class="p">,</span></div><div class='line' id='LC80'>	<span class="p">);</span></div><div class='line' id='LC81'><br/></div><div class='line' id='LC82'><span class="k">if</span> <span class="p">(</span><span class="nb">defined</span> <span class="nv">$opt</span><span class="p">{</span><span class="s">&#39;help&#39;</span><span class="p">}</span> <span class="o">&amp;&amp;</span> <span class="nv">$opt</span><span class="p">{</span><span class="s">&#39;help&#39;</span><span class="p">}</span> <span class="o">==</span> <span class="mi">1</span><span class="p">)</span> <span class="p">{</span> <span class="n">usage</span><span class="p">();</span> <span class="p">}</span></div><div class='line' id='LC83'><br/></div><div class='line' id='LC84'><span class="k">sub </span><span class="nf">usage</span> <span class="p">{</span></div><div class='line' id='LC85'>	<span class="c1"># Shown with --help option passed</span></div><div class='line' id='LC86'>	<span class="k">print</span> <span class="s">&quot;\n&quot;</span><span class="o">.</span></div><div class='line' id='LC87'>		<span class="s">&quot;   MySQLTuner $tunerversion - MySQL High Performance Tuning Script\n&quot;</span><span class="o">.</span></div><div class='line' id='LC88'>		<span class="s">&quot;   Bug reports, feature requests, and downloads at http://mysqltuner.com/\n&quot;</span><span class="o">.</span></div><div class='line' id='LC89'>		<span class="s">&quot;   Maintained by Major Hayden (major\@mhtx.net) - Licensed under GPL\n&quot;</span><span class="o">.</span></div><div class='line' id='LC90'>		<span class="s">&quot;\n&quot;</span><span class="o">.</span></div><div class='line' id='LC91'>		<span class="s">&quot;   Important Usage Guidelines:\n&quot;</span><span class="o">.</span></div><div class='line' id='LC92'>		<span class="s">&quot;      To run the script with the default options, run the script without arguments\n&quot;</span><span class="o">.</span></div><div class='line' id='LC93'>		<span class="s">&quot;      Allow MySQL server to run for at least 24-48 hours before trusting suggestions\n&quot;</span><span class="o">.</span></div><div class='line' id='LC94'>		<span class="s">&quot;      Some routines may require root level privileges (script will provide warnings)\n&quot;</span><span class="o">.</span></div><div class='line' id='LC95'>		<span class="s">&quot;      You must provide the remote server&#39;s total memory when connecting to other servers\n&quot;</span><span class="o">.</span></div><div class='line' id='LC96'>		<span class="s">&quot;\n&quot;</span><span class="o">.</span></div><div class='line' id='LC97'>		<span class="s">&quot;   Connection and Authentication\n&quot;</span><span class="o">.</span></div><div class='line' id='LC98'>		<span class="s">&quot;      --host &lt;hostname&gt;    Connect to a remote host to perform tests (default: localhost)\n&quot;</span><span class="o">.</span></div><div class='line' id='LC99'>		<span class="s">&quot;      --socket &lt;socket&gt;    Use a different socket for a local connection\n&quot;</span><span class="o">.</span></div><div class='line' id='LC100'>		<span class="s">&quot;      --port &lt;port&gt;        Port to use for connection (default: 3306)\n&quot;</span><span class="o">.</span></div><div class='line' id='LC101'>		<span class="s">&quot;      --user &lt;username&gt;    Username to use for authentication\n&quot;</span><span class="o">.</span></div><div class='line' id='LC102'>		<span class="s">&quot;      --pass &lt;password&gt;    Password to use for authentication\n&quot;</span><span class="o">.</span></div><div class='line' id='LC103'>		<span class="s">&quot;\n&quot;</span><span class="o">.</span></div><div class='line' id='LC104'>		<span class="s">&quot;   Performance and Reporting Options\n&quot;</span><span class="o">.</span></div><div class='line' id='LC105'>		<span class="s">&quot;      --skipsize           Don&#39;t enumerate tables and their types/sizes (default: on)\n&quot;</span><span class="o">.</span></div><div class='line' id='LC106'>		<span class="s">&quot;                             (Recommended for servers with many tables)\n&quot;</span><span class="o">.</span></div><div class='line' id='LC107'>		<span class="s">&quot;      --checkversion       Check for updates to MySQLTuner (default: don&#39;t check)\n&quot;</span><span class="o">.</span></div><div class='line' id='LC108'>		<span class="s">&quot;      --forcemem &lt;size&gt;    Amount of RAM installed in megabytes\n&quot;</span><span class="o">.</span></div><div class='line' id='LC109'>		<span class="s">&quot;      --forceswap &lt;size&gt;   Amount of swap memory configured in megabytes\n&quot;</span><span class="o">.</span></div><div class='line' id='LC110'>		<span class="s">&quot;\n&quot;</span><span class="o">.</span></div><div class='line' id='LC111'>		<span class="s">&quot;   Output Options:\n&quot;</span><span class="o">.</span></div><div class='line' id='LC112'>		<span class="s">&quot;      --nogood             Remov