span class="k">if</span> <span class="p">(</span><span class="o">!</span> <span class="o">-</span><span class="n">e</span> <span class="nv">$command</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC255'>		<span class="n">badprint</span> <span class="s">&quot;Unable to find mysqladmin in your \$PATH.  Is MySQL installed?\n&quot;</span><span class="p">;</span></div><div class='line' id='LC256'>		<span class="nb">exit</span><span class="p">;</span></div><div class='line' id='LC257'>	<span class="p">}</span></div><div class='line' id='LC258'>	<span class="c1"># Are we being asked to connect via a socket?</span></div><div class='line' id='LC259'>	<span class="k">if</span> <span class="p">(</span><span class="nv">$opt</span><span class="p">{</span><span class="nb">socket</span><span class="p">}</span> <span class="ow">ne</span> <span class="mi">0</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC260'>		<span class="nv">$remotestring</span> <span class="o">=</span> <span class="s">&quot; -S $opt{socket}&quot;</span><span class="p">;</span></div><div class='line' id='LC261'>	<span class="p">}</span></div><div class='line' id='LC262'>	<span class="c1"># Are we being asked to connect to a remote server?</span></div><div class='line' id='LC263'>	<span class="k">if</span> <span class="p">(</span><span class="nv">$opt</span><span class="p">{</span><span class="n">host</span><span class="p">}</span> <span class="ow">ne</span> <span class="mi">0</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC264'>		<span class="nb">chomp</span><span class="p">(</span><span class="nv">$opt</span><span class="p">{</span><span class="n">host</span><span class="p">});</span></div><div class='line' id='LC265'>		<span class="nv">$opt</span><span class="p">{</span><span class="n">port</span><span class="p">}</span> <span class="o">=</span> <span class="p">(</span><span class="nv">$opt</span><span class="p">{</span><span class="n">port</span><span class="p">}</span> <span class="ow">eq</span> <span class="mi">0</span><span class="p">)?</span> <span class="mi">3306</span> <span class="p">:</span> <span class="nv">$opt</span><span class="p">{</span><span class="n">port</span><span class="p">}</span> <span class="p">;</span></div><div class='line' id='LC266'>		<span class="c1"># If we&#39;re doing a remote connection, but forcemem wasn&#39;t specified, we need to exit</span></div><div class='line' id='LC267'>		<span class="k">if</span> <span class="p">(</span><span class="nv">$opt</span><span class="p">{</span><span class="s">&#39;forcemem&#39;</span><span class="p">}</span> <span class="ow">eq</span> <span class="mi">0</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC268'>			<span class="n">badprint</span> <span class="s">&quot;The --forcemem option is required for remote connections\n&quot;</span><span class="p">;</span></div><div class='line' id='LC269'>			<span class="nb">exit</span><span class="p">;</span></div><div class='line' id='LC270'>		<span class="p">}</span></div><div class='line' id='LC271'>		<span class="n">infoprint</span> <span class="s">&quot;Performing tests on $opt{host}:$opt{port}\n&quot;</span><span class="p">;</span></div><div class='line' id='LC272'>		<span class="nv">$remotestring</span> <span class="o">=</span> <span class="s">&quot; -h $opt{host} -P $opt{port}&quot;</span><span class="p">;</span></div><div class='line' id='LC273'>		<span class="nv">$doremote</span> <span class="o">=</span> <span class="mi">1</span><span class="p">;</span></div><div class='line' id='LC274'>	<span class="p">}</span></div><div class='line' id='LC275'>	<span class="c1"># Did we already get a username and password passed on the command line?</span></div><div class='line' id='LC276'>	<span class="k">if</span> <span class="p">(</span><span class="nv">$opt</span><span class="p">{</span><span class="n">user</span><span class="p">}</span> <span class="ow">ne</span> <span class="mi">0</span> <span class="ow">and</span> <span class="nv">$opt</span><span class="p">{</span><span class="n">pass</span><span class="p">}</span> <span class="ow">ne</span> <span class="mi">0</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC277'>		<span class="nv">$mysqllogin</span> <span class="o">=</span> <span class="s">&quot;-u $opt{user} -p&#39;$opt{pass}&#39;&quot;</span><span class="o">.</span><span class="nv">$remotestring</span><span class="p">;</span></div><div class='line' id='LC278'>		<span class="k">my</span> <span class="nv">$loginstatus</span> <span class="o">=</span> <span class="sb">`mysqladmin ping $mysqllogin 2&gt;&amp;1`</span><span class="p">;</span></div><div class='line' id='LC279'>		<span class="k">if</span> <span class="p">(</span><span class="nv">$loginstatus</span> <span class="o">=~</span><span class="sr"> /mysqld is alive/</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC280'>			<span class="n">goodprint</span> <span class="s">&quot;Logged in using credentials passed on the command line\n&quot;</span><span class="p">;</span></div><div class='line' id='LC281'>			<span class="k">return</span> <span class="mi">1</span><span class="p">;</span></div><div class='line' id='LC282'>		<span class="p">}</span> <span class="k">else</span> <span class="p">{</span></div><div class='line' id='LC283'>			<span class="n">badprint</span> <span class="s">&quot;Attempted to use login credentials, but they were invalid\n&quot;</span><span class="p">;</span></div><div class='line' id='LC284'>			<span class="nb">exit</span> <span class="mi">0</span><span class="p">;</span></div><div class='line' id='LC285'>		<span class="p">}</span></div><div class='line' id='LC286'>	<span class="p">}</span></div><div class='line' id='LC287'>	<span class="k">if</span> <span class="p">(</span> <span class="o">-</span><span class="n">r</span> <span class="s">&quot;/etc/psa/.psa.shadow&quot;</span> <span class="ow">and</span> <span class="nv">$doremote</span> <span class="o">==</span> <span class="mi">0</span> <span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC288'>		<span class="c1"># It&#39;s a Plesk box, use the available credentials</span></div><div class='line' id='LC289'>		<span class="nv">$mysqllogin</span> <span class="o">=</span> <span class="s">&quot;-u admin -p`cat /etc/psa/.psa.shadow`&quot;</span><span class="p">;</span></div><div class='line' id='LC290'>		<span class="k">my</span> <span class="nv">$loginstatus</span> <span class="o">=</span> <span class="sb">`mysqladmin ping $mysqllogin 2&gt;&amp;1`</span><span class="p">;</span></div><div class='line' id='LC291'>		<span class="k">unless</span> <span class="p">(</span><span class="nv">$loginstatus</span> <span class="o">=~</span><span class="sr"> /mysqld is alive/</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC292'>			<span class="n">badprint</span> <span class="s">&quot;Attempted to use login credentials from Plesk, but they failed.\n&quot;</span><span class="p">;</span></div><div class='line' id='LC293'>			<span class="nb">exit</span> <span class="mi">0</span><span class="p">;</span></div><div class='line' id='LC294'>		<span class="p">}</span></div><div class='line' id='LC295'>	<span class="p">}</span> <span class="k">elsif</span> <span class="p">(</span> <span class="o">-</span><span class="n">r</span> <span class="s">&quot;/etc/mysql/debian.cnf&quot;</span> <span class="ow">and</span> <span class="nv">$doremote</span> <span class="o">==</span> <span class="mi">0</span> <span class="p">){</span></div><div class='line' id='LC296'>		<span class="c1"># We have a debian maintenance account, use it</span></div><div class='line' id='LC297'>		<span class="nv">$mysqllogin</span> <span class="o">=</span> <span class="s">&quot;--defaults-file=/etc/mysql/debian.cnf&quot;</span><span class="p">;</span></div><div class='line' id='LC298'>		<span class="k">my</span> <span class="nv">$loginstatus</span> <span class="o">=</span> <span class="sb">`mysqladmin $mysqllogin ping 2&gt;&amp;1`</span><span class="p">;</span></div><div class='line' id='LC299'>		<span class="k">if</span> <span class="p">(</span><span class="nv">$loginstatus</span> <span class="o">=~</span><span class="sr"> /mysqld is alive/</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC300'>			<span class="n">goodprint</span> <span class="s">&quot;Logged in using credentials from debian maintenance account.\n&quot;</span><span class="p">;</span></div><div class='line' id='LC301'>			<span class="k">return</span> <span class="mi">1</span><span class="p">;</span></div><div class='line' id='LC302'>		<span class="p">}</span> <span class="k">else</span> <span class="p">{</span></div><div class='line' id='LC303'>			<span class="n">badprint</span> <span class="s">&quot;Attempted to use login credentials from debian maintenance account, but they failed.\n&quot;</span><span class="p">;</span></div><div class='line' id='LC304'>			<span class="nb">exit</span> <span class="mi">0</span><span class="p">;</span></div><div class='line' id='LC305'>		<span class="p">}</span></div><div class='line' id='LC306'>	<span class="p">}</span> <span class="k">else</span> <span class="p">{</span></div><div class='line' id='LC307'>		<span class="c1"># It&#39;s not Plesk or debian, we should try a login</span></div><div class='line' id='LC308'>		<span class="k">my</span> <span class="nv">$loginstatus</span> <span class="o">=</span> <span class="sb">`mysqladmin $remotestring ping 2&gt;&amp;1`</span><span class="p">;</span></div><div class='line' id='LC309'>		<span class="k">if</span> <span class="p">(</span><span class="nv">$loginstatus</span> <span class="o">=~</span><span class="sr"> /mysqld is alive/</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC310'>			<span class="c1"># Login went just fine</span></div><div class='line' id='LC311'>			<span class="nv">$mysqllogin</span> <span class="o">=</span> <span class="s">&quot; $remotestring &quot;</span><span class="p">;</span></div><div class='line' id='LC312'>			<span class="c1"># Did this go well because of a .my.cnf file or is there no password set?</span></div><div class='line' id='LC313'>			<span class="k">my</span> <span class="nv">$userpath</span> <span class="o">=</span> <span class="sb">`printenv HOME`</span><span class="p">;</span></div><div class='line' id='LC314'>			<span class="k">if</span> <span class="p">(</span><span class="nb">length</span><span class="p">(</span><span class="nv">$userpath</span><span class="p">)</span> <span class="o">&gt;</span> <span class="mi">0</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC315'>				<span class="nb">chomp</span><span class="p">(</span><span class="nv">$userpath</span><span class="p">);</span></div><div class='line' id='LC316'>			<span class="p">}</span></div><div class='line' id='LC317'>			<span class="k">unless</span> <span class="p">(</span> <span class="o">-</span><span class="n">e</span> <span class="s">&quot;${userpath}/.my.cnf&quot;</span> <span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC318'>				<span class="n">badprint</span> <span class="s">&quot;Successfully authenticated with no password - SECURITY RISK!\n&quot;</span><span class="p">;</span></div><div class='line' id='LC319'>			<span class="p">}</span></div><div class='line' id='LC320'>			<span class="k">return</span> <span class="mi">1</span><span class="p">;</span></div><div class='line' id='LC321'>		<span class="p">}</span> <span class="k">else</span> <span class="p">{</span></div><div class='line' id='LC322'>			<span class="k">print</span> <span class="bp">STDERR</span> <span class="s">&quot;Please enter your MySQL administrative login: &quot;</span><span class="p">;</span></div><div class='line' id='LC323'>			<span class="k">my</span> <span class="nv">$name</span> <span class="o">=</span> <span class="o">&lt;&gt;</span><span class="p">;</span></div><div class='line' id='LC324'>			<span class="k">print</span> <span class="bp">STDERR</span> <span class="s">&quot;Please enter your MySQL administrative password: &quot;</span><span class="p">;</span></div><div class='line' id='LC325'>			<span class="nb">system</span><span class="p">(</span><span class="s">&quot;stty -echo &gt;$devnull 2&gt;&amp;1&quot;</span><span class="p">);</span></div><div class='line' id='LC326'>			<span class="k">my</span> <span class="nv">$password</span> <span class="o">=</span> <span class="o">&lt;&gt;</span><span class="p">;</span></div><div class='line' id='LC327'>			<span class="nb">system</span><span class="p">(</span><span class="s">&quot;stty echo &gt;$devnull 2&gt;&amp;1&quot;</span><span class="p">);</span></div><div class='line' id='LC328'>			<span class="nb">chomp</span><span class="p">(</span><span class="nv">$password</span><span class="p">);</span></div><div class='line' id='LC329'>			<span class="nb">chomp</span><span class="p">(</span><span class="nv">$name</span><span class="p">);</span></div><div class='line' id='LC330'>			<span class="nv">$mysqllogin</span> <span class="o">=</span> <span class="s">&quot;-u $name&quot;</span><span class="p">;</span></div><div class='line' id='LC331'>			<span class="k">if</span> <span class="p">(</span><span class="nb">length</span><span class="p">(</span><span class="nv">$password</span><span class="p">)</span> <span class="o">&gt;</span> <span class="mi">0</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC332'>				<span class="nv">$mysqllogin</span> <span class="o">.=</span> <span class="s">&quot; -p&#39;$password&#39;&quot;</span><span class="p">;</span></div><div class='line' id='LC333'>			<span class="p">}</span></div><div class='line' id='LC334'>			<span class="nv">$mysqllogin</span> <span class="o">.=</span> <span class="nv">$remotestring</span><span class="p">;</span></div><div class='line' id='LC335'>			<span class="k">my</span> <span class="nv">$loginstatus</span> <span class="o">=</span> <span class="sb">`mysqladmin ping $mysqllogin 2&gt;&amp;1`</span><span class="p">;</span></div><div class='line' id='LC336'>			<span class="k">if</span> <span class="p">(</span><span class="nv">$loginstatus</span> <span class="o">=~</span><span class="sr"> /mysqld is alive/</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC337'>				<span class="k">print</span> <span class="bp">STDERR</span> <span class="s">&quot;\n&quot;</span><span class="p">;</span></div><div class='line' id='LC338'>				<span class="k">if</span> <span class="p">(</span><span class="o">!</span> <span class="nb">length</span><span class="p">(</span><span class="nv">$password</span><span class="p">))</span> <span class="p">{</span></div><div class='line' id='LC339'>					<span class="c1"># Did this go well because of a .my.cnf file or is there no password set?</span></div><div class='line' id='LC340'>					<span class="k">my</span> <span class="nv">$userpath</span> <span class="o">=</span> <span class="sb">`ls -d ~`</span><span class="p">;</span></div><div class='line' id='LC341'>					<span class="nb">chomp</span><span class="p">(</span><span class="nv">$userpath</span><span class="p">);</span></div><div class='line' id='LC342'>					<span class="k">unless</span> <span class="p">(</span> <span class="o">-</span><span class="n">e</span> <span class="s">&quot;$userpath/.my.cnf&quot;</span> <span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC343'>						<span class="n">badprint</span> <span class="s">&quot;Successfully authenticated with no password - SECURITY RISK!\n&quot;</span><span class="p">;</span></div><div class='line' id='LC344'>					<span class="p">}</span></div><div class='line' id='LC345'>				<span class="p">}</span></div><div class='line' id='LC346'>				<span class="k">return</span> <span class="mi">1</span><span class="p">;</span></div><div class='line' id='LC347'>			<span class="p">}</span> <span class="k">else</span> <span class="p">{</span></div><div class='line' id='LC348'>				<span class="k">print</span> <span class="s">&quot;\n&quot;</span><span class="o">.</span><span class="nv">$bad</span><span class="o">.</span><span class="s">&quot; Attempted to use login credentials, but they were invalid.\n&quot;</span><span class="p">;</span></div><div class='line' id='LC349'>				<span class="nb">exit</span> <span class="mi">0</span><span class="p">;</span></div><div class='line' id='LC350'>			<span class="p">}</span></div><div class='line' id='LC351'>			<span class="nb">exit</span> <span class="mi">0</span><span class="p">;</span></div><div class='line' id='LC352'>		<span class="p">}</span></div><div class='line' id='LC353'>	<span class="p">}</span></div><div class='line' id='LC354'><span class="p">}</span></div><div class='line' id='LC355'><br/></div><div class='line' id='LC356'><span class="c1"># Populates all of the variable and status hashes</span></div><div class='line' id='LC357'><span class="k">my</span> <span class="p">(</span><span class="nv">%mystat</span><span class="p">,</span><span class="nv">%myvar</span><span class="p">,</span><span class="nv">$dummyselect</span><span class="p">);</span></div><div class='line' id='LC358'><span class="k">sub </span><span class="nf">get_all_vars</span> <span class="p">{</span></div><div class='line' id='LC359'>	<span class="c1"># We need to initiate at least one query so that our data is useable</span></div><div class='line' id='LC360'>	<span class="nv">$dummyselect</span> <span class="o">=</span> <span class="sb">`mysql $mysqllogin -Bse &quot;SELECT VERSION();&quot;`</span><span class="p">;</span></div><div class='line' id='LC361'>	<span class="k">my</span> <span class="nv">@mysqlvarlist</span> <span class="o">=</span> <span class="sb">`mysql $mysqllogin -Bse &quot;SHOW /*!50000 GLOBAL */ VARIABLES;&quot;`</span><span class="p">;</span></div><div class='line' id='LC362'>	<span class="k">foreach</span> <span class="k">my</span> <span class="nv">$line</span> <span class="p">(</span><span class="nv">@mysqlvarlist</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC363'>		<span class="nv">$line</span> <span class="o">=~</span><span class="sr"> /([a-zA-Z_]*)\s*(.*)/</span><span class="p">;</span></div><div class='line' id='LC364'>		<span class="nv">$myvar</span><span class="p">{</span><span class="nv">$1</span><span class="p">}</span> <span class="o">=</span> <span class="nv">$2</span><span class="p">;</span></div><div class='line' id='LC365'>	<span class="p">}</span></div><div class='line' id='LC366'>	<span class="k">my</span> <span class="nv">@mysqlstatlist</span> <span class="o">=</span> <span class="sb">`mysql $mysqllogin -Bse &quot;SHOW /*!50000 GLOBAL */ STATUS;&quot;`</span><span class="p">;</span></div><div class='line' id='LC367'>	<span class="k">foreach</span> <span class="k">my</span> <span class="nv">$line</span> <span class="p">(</span><span class="nv">@mysqlstatlist</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC368'>		<span class="nv">$line</span> <span class="o">=~</span><span class="sr"> /([a-zA-Z_]*)\s*(.*)/</span><span class="p">;</span></div><div class='line' id='LC369'>		<span class="nv">$mystat</span><span class="p">{</span><span class="nv">$1</span><span class="p">}</span> <span class="o">=</span> <span class="nv">$2</span><span class="p">;</span></div><div class='line' id='LC370'>	<span class="p">}</span></div><div class='line' id='LC371'>	<span class="c1"># Workaround for MySQL bug #59393 wrt. ignore-builtin-innodb</span></div><div class='line' id='LC372'>	<span class="k">if</span> <span class="p">((</span><span class="nv">$myvar</span><span class="p">{</span><span class="s">&#39;ignore_builtin_innodb&#39;</span><span class="p">}</span> <span class="o">||</span> <span class="s">&quot;&quot;</span><span class="p">)</span> <span class="ow">eq</span> <span class="s">&quot;ON&quot;</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC373'>		<span class="nv">$myvar</span><span class="p">{</span><span class="s">&#39;have_innodb&#39;</span><span class="p">}</span> <span class="o">=</span> <span class="s">&quot;NO&quot;</span><span class="p">;</span></div><div class='line' id='LC374'>	<span class="p">}</span></div><div class='line' id='LC375'>	<span class="c1"># have_* for engines is deprecated and will be removed in MySQL 5.6;</span></div><div class='line' id='LC376'>	<span class="c1"># check SHOW ENGINES and set corresponding old style variables.</span></div><div class='line' id='LC377'>	<span class="c1"># Also works around MySQL bug #59393 wrt. skip-innodb</span></div><div class='line' id='LC378'>	<span class="k">my</span> <span class="nv">@mysqlenginelist</span> <span class="o">=</span> <span class="sb">`mysql $mysqllogin -Bse &quot;SHOW ENGINES;&quot; 2&gt;$devnull`</span><span class="p">;</span></div><div class='line' id='LC379'>	<span class="k">foreach</span> <span class="k">my</span> <span class="nv">$line</span> <span class="p">(</span><span class="nv">@mysqlenginelist</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC380'>		<span class="k">if</span> <span class="p">(</span><span class="nv">$line</span> <span class="o">=~</span><span class="sr"> /^([a-zA-Z_]+)\s+(\S+)/</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC381'>			<span class="k">my</span> <span class="nv">$engine</span> <span class="o">=</span> <span class="nb">lc</span><span class="p">(</span><span class="nv">$1</span><span class="p">);</span></div><div class='line' id='LC382'>			<span class="k">if</span> <span class="p">(</span><span class="nv">$engine</span> <span class="ow">eq</span> <span class="s">&quot;federated&quot;</span> <span class="o">||</span> <span class="nv">$engine</span> <span class="ow">eq</span> <span class="s">&quot;blackhole&quot;</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC383'>				<span class="nv">$engine</span> <span class="o">.=</span> <span class="s">&quot;_engine&quot;</span><span class="p">;</span></div><div class='line' id='LC384'>			<span class="p">}</span> <span class="k">elsif</span> <span class="p">(</span><span class="nv">$engine</span> <span class="ow">eq</span> <span class="s">&quot;berkeleydb&quot;</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC385'>				<span class="nv">$engine</span> <span class="o">=</span> <span class="s">&quot;bdb&quot;</span><span class="p">;</span></div><div class='line' id='LC386'>			<span class="p">}</span></div><div class='line' id='LC387'>			<span class="k">my</span> <span class="nv">$val</span> <span class="o">=</span> <span class="p">(</span><span class="nv">$2</span> <span class="ow">eq</span> <span class="s">&quot;DEFAULT&quot;</span><span class="p">)</span> <span class="p">?</span> <span class="s">&quot;YES&quot;</span> <span class="p">:</span> <span class="nv">$2</span><span class="p">;</span></div><div class='line' id='LC388'>			<span class="nv">$myvar</span><span class="p">{</span><span class="s">&quot;have_$engine&quot;</span><span class="p">}</span> <span class="o">=</span> <span class="nv">$val</span><span class="p">;</span></div><div class='line' id='LC389'>		<span class="p">}</span></div><div class='line' id='LC390'>	<span class="p">}</span></div><div class='line' id='LC391'><span class="p">}</span></div><div class='line' id='LC392'><br/></div><div class='line' id='LC393'><span class="k">sub </span><span class="nf">security_recommendations</span> <span class="p">{</span></div><div class='line' id='LC394'>	<span class="k">print</span> <span class="s">&quot;\n-------- Security Recommendations  -------------------------------------------\n&quot;</span><span class="p">;</span></div><div class='line' id='LC395'>	<span class="k">my</span> <span class="nv">@mysqlstatlist</span> <span class="o">=</span> <span class="sb">`mysql $mysqllogin -Bse &quot;SELECT CONCAT(user, &#39;\@&#39;, host) FROM mysql.user WHERE password = &#39;&#39; OR password IS NULL;&quot;`</span><span class="p">;</span></div><div class='line' id='LC396'>	<span class="k">if</span> <span class="p">(</span><span class="nv">@mysqlstatlist</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC397'>		<span class="k">foreach</span> <span class="k">my</span> <span class="nv">$line</span> <span class="p">(</span><span class="nb">sort</span> <span class="nv">@mysqlstatlist</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC398'>			<span class="nb">chomp</span><span class="p">(</span><span class="nv">$line</span><span class="p">);</span></div><div class='line' id='LC399'>			<span class="n">badprint</span> <span class="s">&quot;User &#39;&quot;</span><span class="o">.</span><span class="nv">$line</span><span class="o">.</span><span class="s">&quot;&#39; has no password set.\n&quot;</span><span class="p">;</span></div><div class='line' id='LC400'>		<span class="p">}</span></div><div class='line' id='LC401'>	<span class="p">}</span> <span class="k">else</span> <span class="p">{</span></div><div class='line' id='LC402'>		<span class="n">goodprint</span> <span class="s">&quot;All database users have passwords assigned\n&quot;</span><span class="p">;</span></div><div class='line' id='LC403'>	<span class="p">}</span></div><div class='line' id='LC404'><span class="p">}</span></div><div class='line' id='LC405'><br/></div><div class='line' id='LC406'><span class="k">sub </span><span class="nf">get_replication_status</span> <span class="p">{</span></div><div class='line' id='LC407'>	<span class="k">my</span> <span class="nv">$slave_status</span> <span class="o">=</span> <span class="sb">`mysql $mysqllogin -Bse &quot;show slave status\\G&quot;`</span><span class="p">;</span></div><div class='line' id='LC408'>	<span class="k">my</span> <span class="p">(</span><span class="nv">$io_running</span><span class="p">)</span> <span class="o">=</span> <span class="p">(</span><span class="nv">$slave_status</span> <span class="o">=~</span><span class="sr"> /slave_io_running\S*\s+(\S+)/i</span><span class="p">);</span></div><div class='line' id='LC409'>	<span class="k">my</span> <span class="p">(</span><span class="nv">$sql_running</span><span class="p">)</span> <span class="o">=</span> <span class="p">(</span><span class="nv">$slave_status</span> <span class="o">=~</span><span class="sr"> /slave_sql_running\S*\s+(\S+)/i</span><span class="p">);</span></div><div class='line' id='LC410'>	<span class="k">if</span> <span class="p">(</span><span class="nv">$io_running</span> <span class="ow">eq</span> <span class="s">&#39;Yes&#39;</span> <span class="o">&amp;&amp;</span> <span class="nv">$sql_running</span> <span class="ow">eq</span> <span class="s">&#39;Yes&#39;</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC411'>		<span class="k">if</span> <span class="p">(</span><span class="nv">$myvar</span><span class="p">{</span><span class="s">&#39;read_only&#39;</span><span class="p">}</span> <span class="ow">eq</span> <span class="s">&#39;OFF&#39;</span><span class="p">)</span> <span class="p">{</span></div><div class='line' id='LC412'>			<span class="n">badprint</span> <span class="s">&quot;This replication slave is running with the read_only option disabled.&quot;</span><span class="p">;</span></div><div class='line' id='LC413'>		<span class="p">}</span> <span class="k">else</span> <span class="p">{</span></div><div class='line' id='LC414'>			<span class="n">goodprint</span> <span class="s">&quot;This replication slave is running with the read_only option enabled.&quot;</span><span class="p">;</span></div><div class='line' id='L